import React, { Component , useState} from 'react';

const API_key = "a89b2a2225b9c2590d9d3de4f9596d31";
const base_url = "https://api.themoviedb.org/3/";
const base_img = "https://image.tmdb.org/t/p/w500/";
const search_movies = "https://api.themoviedb.org/3/search/movie";

const MovieSearch = () => {
    const [query, setQuery] = useState('');
    const [movies, setMovies] = useState([]);

    const searchMovies = async () => {
        try {
            const response = await fetch(`${search_movies}?api_key=${API_key}&query=${query}`);
            const data = await response.json();
            setMovies(data.results);
        } catch (error) {
            console.error("Error fetching data:", error);
        }
    };


     return (
            <div>
                <div className="container-fluid tm-container-content tm-mt-60">
                    <div className="row mb-4">
                        <h2 className="col-6 tm-text-primary text-center">
                            Movie Search
                        </h2>

                        <form className="d-flex tm-search-form w-75">
                            <input className="form-control tm-search-input m-1"
                                   type="text"
                                   placeholder="Search for movies"
                                   value={query}
                                   onChange={(e) => setQuery(e.target.value)}
                            />
                            <button className="btn btn-success tm-search-btn m-1"
                                    type="submit"
                                    onClick={searchMovies}>Search
                            >
                                <i className="fas fa-search"></i>
                            </button>
                        </form>

                        <div className="col-6 d-flex justify-content-end align-items-center">
                            {movies.map(movie => (
                                <div key={movie.id}>
                                    <h4>{movie.title}</h4>
                                    <figure className="effect-ming tm-video-item">
                                    <img src={`${base_img}${movie.poster_path}`} alt={movie.title} />
                                        <figcaption className="d-flex align-items-center justify-content-center">

                                        <p>{movie.overview}</p>
                                        </figcaption>
                                    </figure>
                                </div>
                            ))}
                        </div>
                    </div>

                    };

                        </div>



            </div>
    );

                        }
export default searchMovies;
