import React, {Component ,useState} from 'react';

const API_key = "a89b2a2225b9c2590d9d3de4f9596d31";
const base_url = "https://api.themoviedb.org/3/";
const base_img = "https://image.tmdb.org/t/p/w500/";
const search_movies = "https://api.themoviedb.org/3/search/movie";

const MovieSearch = () => {
    const [query, setQuery] = useState('');
    const [movies, setMovies] = useState([]);

    const searchMovies = async () => {
        try {

            const response = await fetch(`${search_movies}?api_key=${API_key}&query=${query}`);
            const data = await response.json();
            setMovies(data.results);
        } catch (error) {
            console.error("Error fetching data:", error);
        }
    };

// class Navbar extends Component {



        return (
<div>

    <div className="tm-hero d-flex justify-content-center align-items-center " data-parallax="scroll" data-image-src="img/section.jpg">

        <form className="d-flex tm-search-form w-75">
            <input className="form-control tm-search-input m-1"
                   type="text"
                   placeholder="Search for movies"
                   value={query}
                   onChange={(e) => setQuery(e.target.value)}
            />
            <button className="btn btn-success tm-search-btn m-1"  onClick={searchMovies}>
                <i className="fas fa-search"></i>
            </button>
        </form>
    </div>



            <div>
                <div className="container-fluid tm-container-content tm-mt-60">
                    <div className="row mb-4">
                        <h2 className="col-6 tm-text-primary">
                            Latest Photos
                        </h2>
                    </div>

                    <div className="row tm-mb-90 tm-gallery">
                        <div className="col-xl-3 col-lg-4 col-md-6 col-sm-6 col-12 mb-5">
                            <figure className="effect-ming tm-video-item">
                                <img src="../../assets/img/img-03.jpg" alt="Image" className="img-fluid" />
                                    <figcaption className="d-flex align-items-center justify-content-center">
                                        <h2>Clocks</h2>
                                        <a href="photo-detail.html">View more</a>
                                    </figcaption>
                            </figure>
                            <div className="d-flex justify-content-between tm-text-gray">
                                <span className="tm-text-gray-light">18 Oct 2020</span>
                                <span>9,906 views</span>
                            </div>
                        </div>
                            <div>
                            </div>
                        </div>
                    </div>
                </div>

                        </div>
                        );
                        };

export default MovieSearch;
